    //atributos
    MAZO deck;
    JUGADOR mano;
    JUGADOR manoCasa;

    private boolean primera_mano() {
        int mzo = 0;
        mzo = num_mazos();
        boolean ok = false;
        if (mzo != 0) {
            deck = new MAZO(mzo);
            mano = new JUGADOR();
            manoCasa = new JUGADOR();
            mano.pone_carta(deck.Entrega_Carta());
            manoCasa.pone_carta(deck.Entrega_Carta());
            dibuja_mesa();
            actualiza_contador();
            ok = true;
        } else {
            msjerror.setText("DEBE INGRESAR NUMERO ENTRE 1 Y 4");
            errores.setVisible(true);
            ok = false;
        }
        return ok;
        //textfmazos
    }

    private void actualiza_contador() {
        labelplayer.setText("" + mano.Cuenta_Cartas() + " / " + mano.Cuenta_opcion());
        labelcasa.setText("" + manoCasa.Cuenta_Cartas() + " / " + manoCasa.Cuenta_opcion());
    }

    private void dibuja_mesa() {
        int recorrido = 0;
        do {
            int num = mano.getJugadas(recorrido).getNumero();
            int pinta = mano.getJugadas(recorrido).getTipo();
            dibuja_carta_player(recorrido, num, pinta);
            recorrido++;
        } while ((recorrido < 11) && mano.getJugadas(recorrido) != null);
        recorrido = 0;
        do {
            int num = manoCasa.getJugadas(recorrido).getValor();
            int pinta = manoCasa.getJugadas(recorrido).getTipo();
            dibuja_carta_Casa(recorrido, num, pinta);
            recorrido++;
        } while ((recorrido < 11) && manoCasa.getJugadas(recorrido) != null);
    }

