    private int num_mazos() {
        int num = 0;
        try {
            if (textfmazos.getText() != null && Integer.parseInt(textfmazos.getText()) < 5 && Integer.parseInt(textfmazos.getText()) > 0) {
                num = Integer.parseInt(textfmazos.getText());
            }
        } catch (Exception e) {//fallo en texto = no numerico
            errores.setVisible(true);
            msjerror.setText("numero fuera de rango");
        }
        return num;
    }

    private void bmazoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bmazoActionPerformed

        mano.pone_carta(deck.Entrega_Carta());
        dibuja_mesa();
        actualiza_contador();
        revisa_maximo();

    }//GEN-LAST:event_bmazoActionPerformed

    private void revisa_maximo() {
        int puntos = mano.Cuenta_Cartas();
        if (puntos > 21) {
            bmazo.setEnabled(false);
            bquedo.setEnabled(false);
            mensaje.setVisible(true);
            labelmsj.setText("Te has pasado, Pierdes!");
        }
    }

    private void bquedoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bquedoActionPerformed
        //aca juega la IA
        bmazo.setEnabled(false);
        bquedo.setEnabled(false);
        boolean jugando = true;
        int puntaje_player;
        if (mano.Cuenta_opcion() == 21) {
            puntaje_player = 21;
        } else {
            puntaje_player = mano.Cuenta_Cartas();
        }
        int my_puntaje = manoCasa.Cuenta_Cartas();

        while (jugando == true) {

            if (my_puntaje < puntaje_player) {
                manoCasa.pone_carta(deck.Entrega_Carta());
                dibuja_mesa();
                actualiza_contador();
                my_puntaje = manoCasa.Cuenta_Cartas();
                if (manoCasa.Cuenta_opcion() == 21) {
                    jugando = false;
                    perdedor();
                }
                if (dentro_del_maximo() == false) {
                    jugando = false;
                    ganador();
                }
            } else {
                jugando = false;
                perdedor();
            }
        }
    }//GEN-LAST:event_bquedoActionPerformed

    private boolean dentro_del_maximo() {
        if (manoCasa.Cuenta_Cartas() <= 21) {
            return true;
        } else {
            return false;
        }
    }

    private void ganador() {//gana jugador
        mensaje.setVisible(true);
        labelmsj.setText("has Ganado!");
    }

    private void perdedor() {//gana IA
        mensaje.setVisible(true);
        labelmsj.setText("venci, tu Pierdes!");
    }

